import React, { useState } from 'react';
import { useParams } from 'react-router-dom';

const Single = ({ addToCart, shopItems }) => {
  const { id } = useParams();
  const [mainImage, setMainImage] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [selectedColor, setSelectedColor] = useState(null);

  console.log("Product ID:", id); // Debugging: Check if ID is correct

  const product = shopItems.find(item => item.id === parseInt(id));

  if (!product) return <div>Product not found</div>;

  // Use product images or default images if not available
  const thumbnails = product.images || [
    'https://via.placeholder.com/100',
    'https://via.placeholder.com/100',
    'https://via.placeholder.com/100'
  ];

  // Set the first image as main image if not set
  if (!mainImage && thumbnails.length > 0) {
    setMainImage(thumbnails[0]);
  }

  // Handle add to cart with selected options
  const handleAddToCart = () => {
    addToCart({
      ...product,
      quantity,
      selectedColor
    });
  };

  return (
    <div className="product-container">
      <div className="product-image-section">
        {/* Image Section */}
        <div className="product-thumbnails product-image-s">
          {/* Thumbnails */}
          <div className="product-thumbnails">
            {thumbnails.map((img, index) => (
              <img
                key={index}
                src={img}
                alt="Thumbnail"
                className={`h product-main-image product-thumbnails ${
                  mainImage === img ? "border-red-500" : "border-gray-200"
                }`}
                onClick={() => setMainImage(img)}
              />
            ))}
          </div>
          
          {/* Main Image */}
          <div 
            className="product-main-image flex-1 order-1 lg:order-2 bg-gray-100 rounded-lg overflow-hidden"
            style={{ 
              backgroundImage: `url(${mainImage})`, 
              height: "500px",
              backgroundSize: "contain",
              backgroundRepeat: "no-repeat",
              backgroundPosition: "center"
            }}
          ></div>
        </div>

        {/* Product Info Section */}
        <div className="product-info-section">
          <h2 className="product-title">{product.name}</h2>
          <p className="product-description">
            {product.description || "Nam tempus turpis at metus scelerisque placerat nulla deumantos solicitud felis..."}
          </p>
          
          <div className="delivery-badge">
            <span>FREE DELIVERY</span>
          </div>
          
          <div className="flex items-center gap-3">
            {product.originalPrice && (
              <span className="product-price-original">${product.originalPrice.toFixed(2)}</span>
            )}
            <span className="product-price-current">${product.price.toFixed(2)}</span>
          </div>
          
          {/* Star Rating */}
          <div className="product-rating">
            {[...Array(4)].map((_, i) => (
              <i key={i} className="fas fa-star"></i>
            ))}
            <i className="fas fa-star-half-alt"></i>
          </div>
          
          {/* Color Selection - if product has colors */}
          {product.colors && product.colors.length > 0 && (
            <div className="mt-2">
              <span className="color-selection-title">Select Color:</span>
              <div className="color-options flex gap-2">
                {product.colors.map((color, index) => (
                  <div 
                    key={index} 
                    className={`w-8 h-8 rounded-full cursor-pointer border-2 ${
                      selectedColor === color ? "border-gray-500" : "border-transparent"
                    } hover:border-gray-300 transition-all`}
                    style={{ backgroundColor: color }}
                    onClick={() => setSelectedColor(color)}
                  ></div>
                ))}
              </div>
            </div>
          )}
          
          {/* Quantity & Cart */}
          <div className="quantity-control mt-4">
            <span className="quantity-label">Quantity:</span>
            <div className="quantity-selector flex items-center gap-2">
              <button 
                className="quantity-button w-8 h-8 flex items-center justify-center border border-gray-300 rounded"
                onClick={() => setQuantity(Math.max(1, quantity - 1))}
              >
                -
              </button>
              <span className="quantity-value">{quantity}</span>
              <button 
                className="quantity-button w-8 h-8 flex items-center justify-center border border-gray-300 rounded"
                onClick={() => setQuantity(quantity + 1)}
              >
                +
              </button>
            </div>
          </div>
          
          <button 
            className="add-to-cart-button mt-6 bg-black text-white py-3 px-8 rounded hover:bg-gray-800 transition-colors"
            onClick={handleAddToCart}
          >
            ADD TO CART
          </button>
        </div>
      </div>
    </div>
  );
};

export default Single;
