import React, { useEffect, useState } from "react";
import logo from "../../components/assets/images/logo.png";
import { Link, useHistory } from "react-router-dom";
import AuthService from "../../"; // Adjust the path if needed

const Search = ({ CartItem }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(AuthService.checkAuthStatus());
  const history = useHistory();

  useEffect(() => {
    const handleScroll = () => {
      const search = document.querySelector(".search");
      if (search) {
        search.classList.toggle("active", window.scrollY > 100);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  useEffect(() => {
    setIsLoggedIn(AuthService.checkAuthStatus());
  }, []);

  const handleLogout = async () => {
    try {
      await AuthService.logout();
      setIsLoggedIn(false);
      history.push("/"); // React Router v5 redirect
    } catch (error) {
      console.error("Logout failed:", error);
    }
  };

  return (
    <section className="search">
      <div className="container c_flex">
        <div className="logo width">
          <a href="/">
            <img src={logo} alt="Logo" className="mr-3" />
          </a>
        </div>

        <div className="search-box f_flex">
          <i className="fa fa-search"></i>
          <input type="text" placeholder="Search and hit enter..." />
          <span>All Category</span>
        </div>

        <div className="icon f_flex width">
          <Link to="/wishlist">
            <i className="fa-regular fa-heart icon-circle"></i>
          </Link>

          {isLoggedIn ? (
            <i
              className="fa fa-sign-out icon-circle"
              style={{ cursor: "pointer" }}
              onClick={handleLogout}
              title="Logout"
            ></i>
          ) : (
            <Link to="/register">
              <i className="fa fa-user icon-circle" title="Login/Register"></i>
            </Link>
          )}

          <div className="cart">
            <Link to="/cart">
              <i className="fa fa-shopping-bag icon-circle"></i>
              <span>{CartItem.length === 0 ? "0" : CartItem.length}</span>
            </Link>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Search;
