import React, { useState, useEffect } from 'react';
import { Link } from "react-router-dom";
import "./style.css";
import Logout from '../Autentification/logout';
import { deleteProduct, getAllProducts, addProduct, updateProduct } from '../../services/productService';
import EditProductForm from '../product/EditProductForm';
const AdminDashboard = ({
  shopItems,
  setShopItems
}) => {
  const [products, setProducts] = useState([]);
  const [isOpen, setIsOpen] = useState(false);
  const [isEditOpen, setIsEditOpen] = useState(false);
  const [currentProduct, setCurrentProduct] = useState(null);
  const [newProduct, setNewProduct] = useState({
    name: '',
    price: '',
    category: '',
    description: '',
    discount: '0',
    gender: '',
    image_url: './images/shops/shop-1.png',
    // Default image
    size: 'M',
    stock: 10
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [apiStatus, setApiStatus] = useState('unknown'); // 'unknown', 'connected', 'disconnected'

  // Test API connection function
  const testApiConnection = async () => {
    try {
      setLoading(true);
      // Use the getAllProducts function that's already set up with proper headers
      await getAllProducts();
      setApiStatus('connected');
      alert('Backend API is reachable!');
    } catch (error) {
      setApiStatus('disconnected');
      setError(`API Connection Error: ${error.message}. Please ensure your backend server is running.`);
      alert(`Cannot connect to backend: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  // Fetch products when component mounts
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const data = await getAllProducts();
        setProducts(Array.isArray(data) ? data : data.content || []);
        setApiStatus('connected');

        // Also update shopItems if it's meant to be in sync
        if (setShopItems) {
          setShopItems(Array.isArray(data) ? data : data.content || []);
        }
      } catch (err) {
        setError(`Failed to fetch products: ${err.message}`);
        setApiStatus('disconnected');
      } finally {
        setLoading(false);
      }
    };
    fetchProducts();
  }, [setShopItems]); // Add setShopItems as dependency

  const togglePopup = () => {
    setIsOpen(!isOpen);
    // Reset form when opening/closing
    setNewProduct({
      name: '',
      price: '',
      category: '',
      description: '',
      discount: '0',
      gender: '',
      image_url: './images/shops/shop-1.png',
      size: 'M',
      stock: 10
    });
    setError(null);
  };
  const handleInputChange = e => {
    const {
      name,
      value
    } = e.target;
    setNewProduct({
      ...newProduct,
      [name]: name === 'price' || name === 'stock' ? Number(value) : value
    });
  };
  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    // Map category to gender for consistency with your data model
    let genderValue = 'unisex';
    if (newProduct.category === 'men') genderValue = 'men';
    if (newProduct.category === 'women') genderValue = 'women';
    if (newProduct.category === 'kids') genderValue = 'kids';

    // Create product DTO to match your backend expectations
    const productDTO = {
      name: newProduct.name,
      price: Number(newProduct.price),
      description: newProduct.description,
      discount: Number(newProduct.discount),
      gender: genderValue,
      image_url: newProduct.image_url,
      size: newProduct.size,
      stock: Number(newProduct.stock),
      categoryId: 2 // Using default category ID from your sample data
    };
    try {
      // Use the service function instead of direct fetch
      const addedProduct = await addProduct(productDTO);

      // Update both state arrays with the new product
      setProducts(prevItems => [...prevItems, addedProduct]);
      if (setShopItems) {
