import React, { useState, useEffect } from 'react';
import { Link } from "react-router-dom";
import "./style.css";
import Logout from '../Autentification/logout';
import { deleteProduct, getAllProducts, addProduct, updateProduct } from '../../services/productService';
import EditProductForm from '../product/EditProductForm';

const AdminDashboard = ({ shopItems, setShopItems }) => {
  const [products, setProducts] = useState([]);
  const [isOpen, setIsOpen] = useState(false);
  const [isEditOpen, setIsEditOpen] = useState(false);
  const [currentProduct, setCurrentProduct] = useState(null);
  const [newProduct, setNewProduct] = useState({
    name: '',
    price: '',
    category: '',
    description: '',
    discount: '0',
    gender: '',
    image_url: './images/shops/shop-1.png', // Default image
    size: 'M',
    stock: 10
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [apiStatus, setApiStatus] = useState('unknown'); // 'unknown', 'connected', 'disconnected'

  // Test API connection function
  const testApiConnection = async () => {
    try {
      setLoading(true);
      // Use the getAllProducts function that's already set up with proper headers
      await getAllProducts();
      setApiStatus('connected');
      alert('Backend API is reachable!');
    } catch (error) {
      setApiStatus('disconnected');
      setError(`API Connection Error: ${error.message}. Please ensure your backend server is running.`);
      alert(`Cannot connect to backend: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  // Fetch products when component mounts
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const data = await getAllProducts();
        setProducts(Array.isArray(data) ? data : data.content || []);
        setApiStatus('connected');
        
        // Also update shopItems if it's meant to be in sync
        if (setShopItems) {
          setShopItems(Array.isArray(data) ? data : data.content || []);
        }
      } catch (err) {
        setError(`Failed to fetch products: ${err.message}`);
        setApiStatus('disconnected');
      } finally {
        setLoading(false);
      }
    };
    
    fetchProducts();
  }, [setShopItems]); // Add setShopItems as dependency

  const togglePopup = () => {
    setIsOpen(!isOpen);
    // Reset form when opening/closing
    setNewProduct({
      name: '',
      price: '',
      category: '',
      description: '',
      discount: '0',
      gender: '',
      image_url: './images/shops/shop-1.png',
      size: 'M',
      stock: 10
    });
    setError(null);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct({
      ...newProduct,
      [name]: name === 'price' || name === 'stock' ? Number(value) : value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    // Map category to gender for consistency with your data model
    let genderValue = 'unisex';
    if (newProduct.category === 'men') genderValue = 'men';
    if (newProduct.category === 'women') genderValue = 'women';
    if (newProduct.category === 'kids') genderValue = 'kids';

    // Create product DTO to match your backend expectations
    const productDTO = {
      name: newProduct.name,
      price: Number(newProduct.price),
      description: newProduct.description,
      discount: Number(newProduct.discount),
      gender: genderValue,
      image_url: newProduct.image_url,
      size: newProduct.size,
      stock: Number(newProduct.stock),
      categoryId: 2 // Using default category ID from your sample data
    };

    try {
      // Use the service function instead of direct fetch
      const addedProduct = await addProduct(productDTO);

      // Update both state arrays with the new product
      setProducts(prevItems => [...prevItems, addedProduct]);
      if (setShopItems) {
        setShopItems(prevItems => [...prevItems, addedProduct]);
      }

      // Close the popup
      togglePopup();
    } catch (err) {
      setError(`Failed to add product: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  const handleEditClick = (product) => {
    setCurrentProduct(product);
    setIsEditOpen(true);
  };

  const handleProductUpdated = (updatedProduct) => {
    // Update the product in both state arrays
    setProducts(prevItems =>
      prevItems.map(item => item.id === updatedProduct.id ? updatedProduct : item)
    );
    if (setShopItems) {
      setShopItems(prevItems =>
        prevItems.map(item => item.id === updatedProduct.id ? updatedProduct : item)
      );
    }
  };

  const handleDeleteClick = async (productId) => {
    if (window.confirm('Are you sure you want to delete this product?')) {
      try {
        setLoading(true);
        await deleteProduct(productId);
        
        // Remove the product from both state arrays
        setProducts(prevItems => prevItems.filter(item => item.id !== productId));
        if (setShopItems) {
          setShopItems(prevItems => prevItems.filter(item => item.id !== productId));
        }
      } catch (err) {
        setError(`Failed to delete product: ${err.message}`);
      } finally {
        setLoading(false);
      }
    }
  };

  return (
    <div className='dashboard'>
      <div className='first'>
        <div className="sidebar">
          <h2>Dashboard</h2>
          <Link to="/product">Products</Link>
          <Link to="/countproduct">CountProduct</Link>
          <Link to="/users">Users</Link>
          <Logout/>
        </div>
      </div>
      <div className='second'>
        <div className="table-container">
          {apiStatus === 'disconnected' && (
            <div className="api-warning">
              <p>⚠️ Backend connection problem detected: {error}</p>
              <button onClick={testApiConnection} className="btn primary">
                {loading ? 'Testing...' : 'Test API Connection'}
              </button>
            </div>
          )}
          
          <div className='end'>
            <button 
              className='edit btn-dash' 
              onClick={togglePopup}
              disabled={apiStatus === 'disconnected'}
            >
              Add Product
            </button>
            {isOpen && (
              <div className="popup-overlay">
                <div className="popup-content">
                  {error && <div className="error-message">{error}</div>}
                  <form onSubmit={handleSubmit}>
                    <div className="form-group">
                      <label htmlFor="name">Name</label>
                      <input
                        type="text"
                        name="name"
                        id="name"
                        placeholder="Type product name"
                        value={newProduct.name}
                        onChange={handleInputChange}
                        required
                      />
                    </div>

                    <div className="form-group">
                      <label htmlFor="price">Price</label>
                      <input
                        type="number"
                        name="price"
                        id="price"
                        placeholder="$2999"
                        value={newProduct.price}
                        onChange={handleInputChange}
                        required
                      />
                    </div>

                    <div className="form-group">
                      <label htmlFor="category">Category</label>
                      <select
                        id="category"
                        name="category"
                        value={newProduct.category}
                        onChange={handleInputChange}
                        required
                      >
                        <option value="">Select category</option>
                        <option value="men">Men</option>
                        <option value="women">Women</option>
                        <option value="kids">Kids</option>
                      </select>
                    </div>

                    <div className="form-group">
                      <label htmlFor="description">Description</label>
                      <textarea
                        id="description"
                        name="description"
                        rows="4"
                        placeholder="Enter product description here"
                        value={newProduct.description}
                        onChange={handleInputChange}
                        required
                      ></textarea>
                    </div>

                    <div className="form-group">
                      <label htmlFor="discount">Discount</label>
                      <select
                        id="discount"
                        name="discount"
                        value={newProduct.discount}
                        onChange={handleInputChange}
                      >
                        <option value="0">No</option>
                        <option value="5">5%</option>
                        <option value="10">10%</option>
                        <option value="20">20%</option>
                        <option value="30">30%</option>
                        <option value="40">40%</option>
                        <option value="50">50%</option>
                      </select>
                    </div>

                    <div className="form-group">
                      <label htmlFor="stock">Stock</label>
                      <input
                        type="number"
                        name="stock"
                        id="stock"
                        placeholder="10"
                        value={newProduct.stock}
                        onChange={handleInputChange}
                        required
                      />
                    </div>

                    <div className="form-buttons">
                      <button
                        type="submit"
                        className="btn primary"
                        disabled={loading}
                      >
                        {loading ? 'Adding...' : 'Add product'}
                      </button>
                      <button
                        type="button"
                        className="btn cancel"
                        onClick={togglePopup}
                      >
                        <svg
                          aria-hidden="true"
                          className="icon"
                          fill="none"
                          stroke="currentColor"
                          viewBox="0 0 24 24"
                        >
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
                        </svg>
                        Cancel
                      </button>
                    </div>
                  </form>
                </div>
              </div>
            )}
          </div>
          <h2>Product table</h2>
          {error && <div className="error-message">{error}</div>}
          {loading && <div className="loading">Loading...</div>}
          <table className="custom-table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Price</th>
                <th>Modify</th>
                <th>Delete</th>
              </tr>
            </thead>
            <tbody>
              {products.length > 0 ? products.map((product) => (
                <tr key={product.id}>
                  <td>#{product.id}</td>
                  <td>{product.name}</td>
                  <td>${product.price}</td>
                  <td>
                    <button
                      className="btn-dash edit"
                      onClick={() => handleEditClick(product)}
                    >
                      <i className="fas fa-edit"></i>
                    </button>
                  </td>
                  <td>
                    <button
                      className="btn-dash red"
                      onClick={() => handleDeleteClick(product.id)}
                    >
                      <i className="fa-solid fa-trash"></i>
                    </button>
                  </td>
                </tr>
              )) : (
                <tr>
                  <td colSpan="5" className="text-center">
                    {loading ? 'Loading products...' : 'No products found'}
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
      
      {isEditOpen && currentProduct && (
        <EditProductForm
          product={currentProduct}
          onClose={() => setIsEditOpen(false)}
          onProductUpdated={handleProductUpdated}
        />
      )}
    </div>
  );
};

export default AdminDashboard;
