import React from "react"
import logo from "../../components/assets/images/logo.png"
import { Link } from "react-router-dom"
import AuthService from "../../components/Autentification/authservice"


const Search = ({ CartItem }) => {
  // fixed Header
  window.addEventListener("scroll", function () {
    const search = document.querySelector(".search")
    search.classList.toggle("active", window.scrollY > 100)
  })

  const handleLogout = async () => {
    try {
      await AuthService.logout();
      window.location.href = '/'; // Redirect to home page after logout
    } catch (error) {
      console.error('Logout failed:', error);
    }
  }

  const isLoggedIn = AuthService.checkAuthStatus();
  const isUser = AuthService.getUserRole() === 'USER';

  return (
    <>
      <section className='search'>
        <div className='container c_flex'>
          <div className='logo width'>
            <a href="/">
            <img src={logo} alt=''className="mr-3" />
            </a>
          </div>

          <div className='search-box f_flex'>
            <i className='fa fa-search'></i>
            <input type='text' placeholder='Search and hit enter...' />
            <span>All Category</span>
          </div>

          <div className='icon f_flex width'>
            
            <Link to='/wishlist'>
            <i className='fa-regular fa-heart icon-circle' ></i>
            </Link>


              
              {/* Only show login/register if NOT logged in */}
              {!isLoggedIn && (
                <Link to='/register'>
                    <i className='fa fa-user icon-circle'></i>
                </Link>
                
              )}

              {/* {isLoggedIn && isUser && ( */}
                <div 
                onClick={handleLogout} 
                style={{ cursor: 'pointer' }}
                title="Logout"
              >
                <i className='fas fa-sign-out-alt icon-circle'></i>
              </div>
              )}

            


            <div className='cart'>
              <Link to='/cart'>
                <i className='fa fa-shopping-bag icon-circle'></i>
                <span>{CartItem.length === 0 ? "0" : CartItem.length}</span>
              </Link>
            </div>
          </div>
        </div>
      </section>
    </>
  )
}

export default Search
