import React from "react";
import { Link } from "react-router-dm";

const ShopCart = ({ shopItems, addToCart, addToWishlist, removeFromWishlist, isInWishlist }) => {
  return (
    <>
      {shopItems.map((item) => {
        const wishlisted = isInWishlist(item.id); // Check if item is in wishlist
        
        return (
          <div className='box' key={item.id}>
            <div className='product mtop'>
              <div className='img'>
                <span className='discount'>{item.discount}% Off</span>
                <Link to={`/product/${item.id}`}>
                  <img src={item.cover} alt={item.name} />
                </Link>
             
                {/* Updated Wishlist Heart */}
                <div className='product-like'>
                  <i
                    className={wishlisted ? "fa-solid fa-heart" : "fa-regular fa-heart"}
                    onClick={() => wishlisted ? removeFromWishlist(item) : addToWishlist(item)}
                    style={{ 
                      color: wishlisted ? "red" : "",
                      cursor: "pointer"
                    }}
                  ></i>
                </div>
              </div>
              
              <div className='product-details'>
                <h3>
                  <Link to={`/product/${item.id}`}>{item.name}</Link>
                </h3>
                <div className='rate'>
                  <i className='fa fa-star'></i>
                  <i className='fa fa-star'></i>
                  <i className='fa fa-star'></i>
                  <i className='fa fa-star'></i>
                  <i className='fa fa-star'></i>
                </div>
                <div className='price'>
                  <h4>${item.price}.00</h4>
                  <button onClick={() => addToCart(item)}>
                    <i className='fa fa-plus'></i>
                  </button>
                </div>
              </div>
            </div>
          </div>
        );
      })}
    </>
  );
};

export default ShopCart;
