import React, { useState, useEffect } from "react"
import logo from "../../components/assets/images/logo.png"
import { Link, useNavigate } from "react-router-dom"
import AuthService from "../../components/Autentification/authservice"

const Search = ({ CartItem }) => {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isUser, setIsUser] = useState(false);

  useEffect(() => {
    // Check auth status when component mounts
    const checkAuth = () => {
      const loggedIn = AuthService.checkAuthStatus();
      const userRole = AuthService.getUserRole();
      setIsLoggedIn(loggedIn);
      setIsUser(userRole === 'USER');
    };

    checkAuth();
  }, []);

  // fixed Header
  useEffect(() => {
    const handleScroll = () => {
      const search = document.querySelector(".search");
      if (search) {
        search.classList.toggle("active", window.scrollY > 100);
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleLogout = async () => {
    try {
      await AuthService.logout();
      setIsLoggedIn(false);
      setIsUser(false);
      navigate('/');
      window.location.reload(); // Force reload after logout
    } catch (error) {
      console.error('Logout failed:', error);
    }
  };

  return (
    <>
      <section className='search'>
        <div className='container c_flex'>
          <div className='logo width'>
            <Link to="/">
              <img src={logo} alt='Logo' className="mr-3" />
            </Link>
          </div>

          <div className='search-box f_flex'>
            <i className='fa fa-search'></i>
            <input type='text' placeholder='Search and hit enter...' />
            <span>All Category</span>
          </div>

          <div className='icon f_flex width'>
            <Link to='/wishlist'>
              <i className='fa-regular fa-heart icon-circle'></i>
            </Link>

            {!isLoggedIn ? (
              <Link to='/register'>
                <i className='fa fa-user icon-circle'></i>
              </Link>
            ) : isUser && (
              <div 
                onClick={handleLogout} 
                style={{ cursor: 'pointer' }}
                title="Logout"
              >
                <i className='fas fa-sign-out-alt icon-circle'></i>
              </div>
            )}

            <div className='cart'>
              <Link to='/cart'>
                <i className='fa fa-shopping-bag icon-circle'></i>
                <span>{CartItem?.length || 0}</span>
              </Link>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default Search;
