import React, { useState } from "react";
import Catg from "./Catg";
import ShopCart from "./ShopCart";
import "./style.css";

const Shop = ({ addToCart, shopItems }) => {
 
  // Ensure the initial category is valid
  const [selectedCategory, setSelectedCategory] = useState("all");

  // Filter products safely
  const filteredItems = 
    selectedCategory === "all"
      ? shopItems
      : shopItems.filter(
          (item) => item.category && item.category.toLowerCase() === selectedCategory
        );
        
          

  return (
    <>
      <section className="shop background">
        <div className="container d_flex">
          {/* Pass setCategory to Catg */}
          <Catg setCategory={setSelectedCategory} />

          <div className="contentWidth">
            <div className="heading d_flex">
              <div className="heading-left row f_flex">
                <h2>Shop</h2>
              </div>
            </div>

            <div className="product-content grid1">
              {/* Pass only the filtered items */}
              <ShopCart addToCart={addToCart} shopItems={filteredItems} />
            </div>
          </div>
        </div>
      </section>


     
    </>
  );
};

export default Shop;
