// src/components/Dashboard/Dashboard.jsx
import React, { useState } from 'react';
import { Link } from "react-router-dom"

import "./style.css";

const Dashboard = ({ shopItems }) => {
  const [isOpen, setIsOpen] = useState(false);

  const togglePopup = () => {
    setIsOpen(!isOpen);
  };
  return (
    <div className='dashboard'>
      <div>
        <button onClick={togglePopup}>Open Popup</button>

        {isOpen && (
          <div className="popup-overlay">
            <div className="popup-content">
              <h2>Popup Title</h2>
              <p>This is a simple popup in React!</p>
              <button onClick={togglePopup}>Close</button>
            </div>
          </div>
        )}
      </div>
      <div className='first'>
        <li>
          <Link to='/'>Acceuil</Link>
        </li>
      </div>
      <div className='second'>
        <div className="table-container">
          <button className='edit btn-dash'>Add Product</button>
          <h2>Product table</h2>
          <table className="custom-table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>price</th>
                <th>Modify</th>
                <th>Delete</th>
              </tr>
            </thead>
            <tbody>
              {shopItems.map((user) => (
                <tr key={user.id}>
                  <td>#{user.id}</td>
                  <td>{user.name}</td>
                  <td>{user.price}</td>
                  <td> <button onClick="" className="btn-dash edit"><i class="fas fa-edit"></i> </button></td>
                  <td> <button onClick="" className="btn-dash red"><i class="fa-solid fa-trash"> </i>  </button></td>

                </tr>
              ))}
            </tbody>
          </table>
        </div>

      </div>


    </div>
  );
};

export default Dashboard;
