import React, { useState } from "react";
import Catg from "./Catg";
import ShopCart from "./ShopCart";
import "./style.css";

const Singleproduct = ({ addToCart, shopItems }) => {
  const [mainImage, setMainImage] = useState("../images/shops/shop-1");
          const [quantity, setQuantity] = useState(1);
          const thumbnails = [
            "../images/shops/shop-1.png",
            
            "../images/shops/shop-1.png",
            "../images/shops/shop-1.png",
          ];
  // Ensure the initial category is valid
  const [selectedCategory, setSelectedCategory] = useState("all");

  // Filter products safely
  const filteredItems = 
    selectedCategory === "all"
      ? shopItems
      : shopItems.filter(
          (item) => item.category && item.category.toLowerCase() === selectedCategory
        );
        
          

  return (
    <>
    


      <div className="flex flex-col lg:flex-row gap-8">
      {/* Image Section */}
      <div className="lg:w-2/3 flex flex-col lg:flex-row gap-4">
        {/* Thumbnails */}
        <div className="flex flex-col gap-2">
          {thumbnails.map((img, index) => (
            <img
              key={index}
              src={img}
              alt="Thumbnail"
              className={`w-16 h-16 cursor-pointer border ${
                mainImage === img ? "border-red-500" : "border-gray-300"
              }`}
              onClick={() => setMainImage(img)}
            />
          ))}
        </div>
        {/* Main Image */}
        <div className="flex-1 bg-cover bg-center" style={{ backgroundImage: `url(${mainImage})`, height: "400px" }}></div>
      </div>

      {/* Product Info Section */}
      <div className="lg:w-1/3 flex flex-col gap-4">
        <h2 className="text-2xl font-bold">Pocket Cotton Sweatshirt</h2>
        <p className="text-gray-600">Nam tempus turpis at metus scelerisque placerat nulla deumantos solicitud felis...</p>
        <div className="flex items-center text-red-500 font-bold">Free Delivery</div>
        <div className="text-gray-400 line-through">$629.99</div>
        <div className="text-2xl font-bold">$495.00</div>
        {/* Star Rating */}
        <div className="flex gap-1 text-yellow-500">
          {[...Array(4)].map((_, i) => (
            <i key={i} className="fa fa-star"></i>
          ))}
          <i className="fa fa-star-o"></i>
        </div>
        {/* Color Selection */}
        <div>
          <span className="font-bold">Select Color:</span>
          <div className="flex gap-2 mt-2">
            {["#e54e5d", "#252525", "#60b3f3"].map((color, index) => (
              <div key={index} className="w-6 h-6 rounded-full cursor-pointer" style={{ background: color }}></div>
            ))}
          </div>
        </div>
        {/* Quantity & Cart */}
        <div className="flex items-center gap-4">
          <span>Quantity:</span>
          <div className="flex items-center border px-2 py-1">
            <button className="px-2" onClick={() => setQuantity(Math.max(1, quantity - 1))}>
              -
            </button>
            <span className="mx-2">{quantity}</span>
            <button className="px-2" onClick={() => setQuantity(quantity + 1)}>
              +
            </button>
          </div>
        </div>
        <button className="bg-red-500 text-white py-2 px-4 rounded mt-4">Add to Cart</button>
      </div>
    </div>
    </>
  );
};

export default Singleproduct;
