import React, { useState, useEffect } from "react"
import logo from "../../components/assets/images/logo.png"
import { Link, useHistory } from "react-router-dom" // React Router v5
import AuthService from "../../components/Autentification/authservice"

const Search = ({ CartItem }) => {


  
  // fixed Header
  window.addEventListener("scroll", function () {
    const search = document.querySelector(".search")
    search.classList.toggle("active", window.scrollY > 100)
  })
  const history = useHistory(); // Change this line
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isUser, setIsUser] = useState(false);

  // Check auth status regularly
  useEffect(() => {
    const checkAuth = () => {
      const loggedIn = sessionStorage.getItem('isLoggedIn') === 'true';
      const userRole = sessionStorage.getItem('userRole');
      setIsLoggedIn(loggedIn);
      setIsUser(userRole === 'USER');
    };

    checkAuth();
    // Check auth status every 30 seconds
    const interval = setInterval(checkAuth, 30000);
    return () => clearInterval(interval);
  }, []);

  const handleLogout = async () => {
    try {
      await AuthService.logout();
      // Clear state
      setIsLoggedIn(false);
      setIsUser(false);
      // Clear session storage
      sessionStorage.removeItem('isLoggedIn');
      sessionStorage.removeItem('userRole');
      // Navigate and reload
      history.push('/'); // Change this line
      window.location.reload();
    } catch (error) {
      console.error('Logout failed:', error);
    }
  };

  return (
    <>
      <section className='search'>
        <div className='container c_flex'>
          {/* ...existing logo and search box code... */}

          <div className='icon f_flex width'>
            <Link to='/wishlist'>
              <i className='fa-regular fa-heart icon-circle'></i>
            </Link>

            {/* Simplified conditional rendering */}
            {isLoggedIn && isUser ? (
              <div 
                onClick={handleLogout} 
                style={{ cursor: 'pointer' }}
                title="Logout"
                className="auth-icon"
              >
                <i className='fas fa-sign-out-alt icon-circle'></i>
              </div>
            ) : (
              <Link to='/register' className="auth-icon">
                <i className='fa fa-user icon-circle'></i>
              </Link>
            )}

            <div className='cart'>
              <Link to='/cart'>
                <i className='fa fa-shopping-bag icon-circle'></i>
                <span>{CartItem?.length || 0}</span>
              </Link>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default Search;
