import React, { useState ,useEffect }  from "react"
import { Link } from "react-router-dom";
import Wishlist from "../wishlist/wishlist";

const ShopCart = ({ shopItems, addToCart, }) => {
    const [wishlistItems, setWishlistItems] = useState([]);
    useEffect(() => {
      const storedWishlist = JSON.parse(localStorage.getItem("wishlist")) || [];
      setWishlistItems(storedWishlist);
    }, []);
  
    // Save wishlist items to localStorage whenever they change
    useEffect(() => {
      localStorage.setItem("wishlist", JSON.stringify(wishlistItems));
    }, [wishlistItems]);
  const addToWishlist = (product) => {
    const productExist = wishlistItems.find((item) => item.id === product.id);
    if (!productExist) {
      setWishlistItems([...wishlistItems, product]);
    }
  };

  const removeFromWishlist = (product) => {
    setWishlistItems(wishlistItems.filter((item) => item.id !== product.id));
  };

  const isInWishlist = (productId) => {
    console.log("Wishlist items:", wishlistItems); // Add this for debugging
    return wishlistItems.some(item => item.id === productId);
  };

  console.log( isInWishlist);


  return (
    <>
      {shopItems.map((item, index) => {
        const wishlisted = isInWishlist(item.id); 
        return (
          <div className='box' key={item.id}>
            <div className='product mtop'>
              <div className='img'>
                <span className='discount'>{item.discount}% Off</span>
                <Link to={`/product/${item.id}`}>
                  <img src={item.cover} alt={item.name} />
                </Link>
             
                <div className='product-like'>
                <i
                  className={isInWishlist(item.id) ? "fa-solid fa-heart" : "fa-regular fa-heart"}
                  onClick={() => 
                    isInWishlist(item.id) 
                      ? removeFromWishlist(item) 
                      : addToWishlist(item)
                  }
                  style={{ 
                    color: isInWishlist(item.id) ? "red" : "",
                    cursor: "pointer" 
                  }}
                ></i>                </div>
              </div>
              <div className='product-details'>
                <h3>
                  <Link to={`/product/${item.id}`}>{item.name}</Link>
                </h3>
                <div className='rate'>
                  <i className='fa fa-star'></i>
                  <i className='fa fa-star'></i>
                  <i className='fa fa-star'></i>
                  <i className='fa fa-star'></i>
                  <i className='fa fa-star'></i>
                </div>
                <div className='price'>
                  <h4>${item.price}.00 </h4>
                  <button onClick={() => addToCart(item)}>
                    <i className='fa fa-plus'></i>
                  </button>
                </div>
                
              </div>
            </div>
            
          </div>
        );
      })}
       <Wishlist
                     shopItems={filteredItems}
                     addToCart={addToCart}
                     addToWishlist={addToWishlist}
                     removeFromWishlist={removeFromWishlist}
                     isInWishlist={isInWishlist}
                   />
    </>
  );
};

export default ShopCart;
