import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Sdata from "./Sdata";

const Single = () => {
    const { id } = useParams();
    const [product, setProduct] = useState(null);
  
    useEffect(() => {
      console.log("Received ID:", id); // Debugging
      console.log("All products:", Sdata.shopItems); // Debugging
      
      const foundProduct = Sdata.shopItems.find(item => item.id === parseInt(id));
      console.log("Found product:", foundProduct); // Debugging
      
      setProduct(foundProduct);
    }, [id]);
  
    if (!product) {
      return <div>Loading product...</div>;
    }

  return (
    <div className="single-product-page">
      <h1>{product.title}</h1>
      {/* You can add more product details here later */}
    </div>
  );
};

export default Single;
