import React, { useState } from "react"
import "./App.css"

import { BrowserRouter as Router, Switch, Route } from "react-router-dom"
import Header from "./common/header/Header"
import Pages from "./pages/Pages"
import Data from "./components/Data"
import Cart from "./common/Cart/Cart"
import Footer from "./common/footer/Footer"
import Sdata from "./components/shops/Sdata"
import Shopp from './components/shopPage/shop'
import Singleproduct from './components/shops/singleproduct'
import Single from './components/shops/single'
import Register from "./components/Autentification/register"
import Signin from "./components/Autentification/Signin"
import Wishlist from "./components/wishlist/wishlist"
import Dashboard from "./components/Dashboard/admindashboard"

import Users from "./components/users/users"




const ProtectedRoute = ({ children }) => {
  const isAuthenticated = AuthService.checkAuthStatus();
  
  if (!isAuthenticated) {
    // Rediriger vers la page de connexion si l'utilisateur n'est pas authentifié
    return <Navigate to="/login" replace />;
  }
  
  return children;
};


// import ShopCart from "./components/shops/ShopCart"

function App() {

  const [CartItem, setCartItem] = useState([])
  const [wishlistItems, setWishlistItems] = useState([])
  const [sidebarOpen, setSidebarOpen] = useState(true);

  const { productItems } = Data
  const { shopItems } = Sdata
  const [sidebarColor, setSidebarColor] = useState('primary');
  const [sidebarType, setSidebarType] = useState('bg-transparent');
  const [navbarFixed, setNavbarFixed] = useState(false);

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  const addToCart = (product) => {
    const productExit = CartItem.find((item) => item.id === product.id)
    if (productExit) {
      setCartItem(CartItem.map((item) => (item.id === product.id ? { ...productExit, qty: productExit.qty + 1 } : item)))
    } else {
      setCartItem([...CartItem, { ...product, qty: 1 }])
    }
  }

  const decreaseQty = (product) => {
    const productExit = CartItem.find((item) => item.id === product.id)
    if (productExit.qty === 1) {
      setCartItem(CartItem.filter((item) => item.id !== product.id))
    } else {
      setCartItem(CartItem.map((item) => (item.id === product.id ? { ...productExit, qty: productExit.qty - 1 } : item)))
    }
  }

  // Wishlist functions
  const addToWishlist = (product) => {
    const productExist = wishlistItems.find((item) => item.id === product.id);
    if (!productExist) {
      setWishlistItems([...wishlistItems, product]);
    }
  };

  const removeFromWishlist = (product) => {
    setWishlistItems(wishlistItems.filter((item) => item.id !== product.id));
  };

  const isInWishlist = (productId) => {
    console.log("Wishlist items:", wishlistItems); // Add this for debugging
    return wishlistItems.some(item => item.id === productId);
  };

  // Right before your return statement in App.js, add:

  return (
    <>
      <Router>
        <Header CartItem={CartItem} wishlistItems={wishlistItems} />
        <Switch>
          <Route path='/' exact>
            <Pages
              productItems={productItems}
              addToCart={addToCart}
              shopItems={shopItems}
              addToWishlist={addToWishlist}
              removeFromWishlist={removeFromWishlist}
              isInWishlist={isInWishlist}
            />
          </Route>

          <Route path='/cart' exact>
            <Cart
              shopItems={shopItems}
              addToCart={addToCart}
              addToWishlist={addToWishlist}
              removeFromWishlist={removeFromWishlist}
              isInWishlist={isInWishlist}
              CartItem={CartItem}
              decreaseQty={decreaseQty}
            />
          </Route>

          <Route path='/shop' exact>
            <Shopp
              shopItems={shopItems}
              addToCart={addToCart}
              addToWishlist={addToWishlist}
              removeFromWishlist={removeFromWishlist}
              isInWishlist={isInWishlist}
              CartItem={CartItem}
              decreaseQty={decreaseQty}
            />
          </Route>

          <Route path='/single' exact>
            <Singleproduct
              addToCart={addToCart}
              shopItems={shopItems}
              addToWishlist={addToWishlist}
              removeFromWishlist={removeFromWishlist}
              isInWishlist={isInWishlist}
            />
          </Route>

          <Route path='/register' exact>
            <Register />
          </Route>
          <Route path='/product' exact>
            <Dashboard shopItems={shopItems}  />
          </Route>
          <Route path='/users' exact>
            <Users shopItems={shopItems}  />
          </Route>

          <Route path='/signin' exact>
            <Signin />
          </Route>

          <Route path='/wishlist' exact>
            <Wishlist
              shopItems={shopItems}
              addToCart={addToCart}
              addToWishlist={addToWishlist}
              removeFromWishlist={removeFromWishlist}
              isInWishlist={isInWishlist}
              CartItem={CartItem}
              decreaseQty={decreaseQty}
              wishlistItems={wishlistItems}
            />
          </Route>

          <Route path="/product/:id" exact render={(props) =>
            <Single
              {...props}
              addToCart={addToCart}
              shopItems={shopItems}
              addToWishlist={addToWishlist}
              removeFromWishlist={removeFromWishlist}
              isInWishlist={isInWishlist}
            />}
          />
          {/* <div className={`g-sidenav-show bg-gray-100 ${sidebarOpen ? '' : 'g-sidenav-hidden'}`}>
            <Sidebar
              sidebarColor={sidebarColor}
              sidebarType={sidebarType}
              toggleSidebar={toggleSidebar}
            />

            <FixedPlugin
              setSidebarColor={setSidebarColor}
              setSidebarType={setSidebarType}
              setNavbarFixed={setNavbarFixed}
            />
          </div> */}
        </Switch>
        <Footer />
      </Router>
    </>
  )
}

export default App
