import React, { useState } from "react";
import Catg from "./Catg";
import ShopCart from "./ShopCart";
import Singleproduct from "./Singleproduct"; // Import the Singleproduct component
import "./style.css";

const Shop = ({ addToCart, shopItems }) => {
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [selectedProduct, setSelectedProduct] = useState(null);

  const filteredItems = 
    selectedCategory === "all"
      ? shopItems
      : shopItems.filter(
          (item) => item.category && item.category.toLowerCase() === selectedCategory
        );

  const handleProductSelect = (product) => {
    setSelectedProduct(product);
  };

  const handleBackToShop = () => {
    setSelectedProduct(null);
  };

  return (
    <>
      <section className="shop background">
        <div className="container d_flex">
          {selectedProduct ? (
            // Show single product view when a product is selected
            <Singleproduct 
              product={selectedProduct} 
              addToCart={addToCart}
              onBack={handleBackToShop}
            />
          ) : (
            // Show shop view when no product is selected
            <>
              <Catg setCategory={setSelectedCategory} />
              <div className="contentWidth">
                <div className="heading d_flex">
                  <div className="heading-left row f_flex">
                    <h2>Shop</h2>
                  </div>
                </div>
                <div className="product-content grid1">
                  <ShopCart 
                    addToCart={addToCart} 
                    shopItems={filteredItems}
                    onProductSelect={handleProductSelect}
                  />
                </div>
              </div>
            </>
          )}
        </div>
      </section>
    </>
  );
};

export default Shop;
