// In your ShopCart.js
const ShopCart = ({ addToCart, shopItems, onProductSelect }) => {
  return (
    <>
      {shopItems.map((item, index) => {
        return (
          <div 
            className="product-cart" 
            key={index}
            onClick={() => onProductSelect(item)} // Add this click handler
          >
            {/* Your existing product card content */}
            <div className="product-like">
              <label>{item.discount || 0}%</label>
            </div>
            <div className="product-img">
              <img src={item.cover} alt={item.name} />
            </div>
            <div className="product-details">
              <h3>{item.name}</h3>
              <div className="rate">${item.price}</div>
              <button onClick={(e) => {
                e.stopPropagation(); // Prevent triggering the parent click
                addToCart(item);
              }}>
                Add to cart
              </button>
            </div>
          </div>
        );
      })}
    </>
  );
};
