import axios from 'axios';

const API_BASE_URL = 'http://localhost:8080/api/products';



// Function to create Basic Auth header
const getBasicAuthHeader = (username, password) => {
  const credentials = btoa(`${username}:${password}`); // Base64 encode username:password
  return { Authorization: `Basic ${credentials}` };
};

// Add a new product
export const addProduct = async (productData, username, password) => {
  try {
    const response = await fetch(API_BASE_URL, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        ...getBasicAuthHeader(username, password), // Add Basic Auth
      },
      body: JSON.stringify(productData),
    });

    if (!response.ok) {
      throw new Error(`Error ${response.status}: ${response.statusText}`);
    }

    return await response.json();
  } catch (error) {
    console.error('Error adding product:', error);
    throw error;
  }
};

// Update an existing product
export const updateProduct = async (productId, productData, username, password) => {
  try {
    const response = await fetch(`${API_BASE_URL}/${productId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        ...getBasicAuthHeader(username, password), // Add Basic Auth
      },
      body: JSON.stringify(productData),
    });

    if (!response.ok) {
      throw new Error(`Error ${response.status}: ${response.statusText}`);
    }

    return await response.json();
  } catch (error) {
    console.error('Error updating product:', error);
    throw error;
  }
};

// Delete a product
export const deleteProduct = async (productId, username, password) => {
  try {
    const response = await fetch(`${API_BASE_URL}/${productId}`, {
      method: 'DELETE',
      headers: {
        ...getBasicAuthHeader(username, password), // Add Basic Auth
      },
    });

    if (!response.ok) {
      throw new Error(`Error ${response.status}: ${response.statusText}`);
    }

    return true;
  } catch (error) {
    console.error('Error deleting product:', error);
    throw error;
  }
};

// Get all products (no auth required for GET)
export const getAllProducts = async () => {
  try {
    const response = await fetch(`${API_BASE_URL}?size=100`);
    if (!response.ok) {
      throw new Error(`Error ${response.status}: ${response.statusText}`);
    }
    return await response.json();
  } catch (error) {
    console.error("Error fetching products:", error);
    throw error;
  }
};






// Method to count products in a specific category
export const countProductsByCategory = async (categoryId) => {
   try {
     const response = await axios.get(`${API_BASE_URL}/count-by-category/${categoryId}`, {
     headers: getAuthHeader()
   });
     return response.data;
    } catch (error) {
      console.error(`Error counting products in category ${categoryId}:`, error.response?.data || error.message);
       if (error.response?.status === 404) {
  
        return 0; // Or handle the not found case as needed
   }
   throw new Error(error.response?.data?.message || `Failed to count products in category ${categoryId}`);
   }
  };
  

  // Function to search products by name
export const searchProductsByName = async (name) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/search`, {
      params: { name },
      headers: getAuthHeader(),
    });
    return response.data;
  } catch (error) {
    console.error("Error searching products by name:", error.response?.data || error.message);
    throw new Error(error.response?.data?.message || 'Failed to search products by name');
  }
};
