import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Sdata from "./Sdata";
import "./style.css";

const Singleproduct = ({ addToCart }) => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [likeCount, setLikeCount] = useState(0);
  const [selectedColor, setSelectedColor] = useState("#e54e5d");
  const [mainImage, setMainImage] = useState("");

  useEffect(() => {
    const foundProduct = Sdata.shopItems.find(item => item.id === parseInt(id));
    if (foundProduct) {
      setProduct(foundProduct);
      setMainImage(foundProduct.cover);
    }
  }, [id]);

  const incrementQuantity = () => setQuantity(prev => prev + 1);
  const decrementQuantity = () => setQuantity(prev => (prev > 1 ? prev - 1 : 1));

  const handleAddToCart = () => {
    if (product) {
      // Add the product to cart with selected quantity
      const itemToAdd = { ...product, quantity, selectedColor };
      addToCart(itemToAdd);
    }
  };

  if (!product) {
    return <div className="loading">Loading...</div>;
  }

  // Calculate discounted price if available
  const originalPrice = parseFloat(product.price);
  const discountPercentage = product.discount ? parseFloat(product.discount) : 0;
  const currentPrice = originalPrice - (originalPrice * discountPercentage) / 100;

  // Sample thumbnails (in a real app, these would come from product data)
  const thumbnails = [
    product.cover,
    "../images/shops/shop-2.png", // Replace with actual additional images
    "../images/shops/shop-3.png",
  ];

  // Sample colors (in a real app, these would come from product data)
  const availableColors = ["#e54e5d", "#252525", "#60b3f3"];

  return (
    <div className="product-container">
      <div className="product-content">
        {/* Image Gallery Section */}
        <div className="product-gallery">
          <div className="main-image-container">
            <img 
              src={mainImage} 
              alt={product.name} 
              className="main-image"
            />
            <div className="like-button" onClick={() => setLikeCount(prev => prev + 1)}>
              <i className={`fa-${likeCount ? 'solid' : 'regular'} fa-heart`}></i>
              {likeCount > 0 && <span className="like-count">{likeCount}</span>}
            </div>
          </div>
          
          <div className="thumbnail-container">
            {thumbnails.map((img, index) => (
              <img
                key={index}
                src={img}
                alt={`Thumbnail ${index + 1}`}
                className={`thumbnail ${mainImage === img ? 'active' : ''}`}
                onClick={() => setMainImage(img)}
              />
            ))}
          </div>
        </div>

        {/* Product Info Section */}
        <div className="product-info">
          <h1 className="product-title">{product.name}</h1>
          
          <div className="price-section">
            {discountPercentage > 0 && (
              <span className="original-price">${originalPrice.toFixed(2)}</span>
            )}
            <span className="current-price">${currentPrice.toFixed(2)}</span>
            {discountPercentage > 0 && (
              <span className="discount-badge">-{discountPercentage}%</span>
            )}
          </div>
          
          <div className="rating-section">
            {[...Array(5)].map((_, i) => (
              <i 
                key={i} 
                className={`fa-star ${i < 4 ? 'fas' : 'fa-regular'}`}
              ></i>
            ))}
            <span className="review-count">(24 reviews)</span>
          </div>
          
          <p className="product-description">
            {product.description || "Premium quality product with excellent craftsmanship."}
          </p>
          
          <div className="delivery-badge">
            <i className="fas fa-truck"></i>
            <span>FREE DELIVERY</span>
          </div>
          
          {/* Color Selection */}
          <div className="color-selection">
            <h3>Color:</h3>
            <div className="color-options">
              {availableColors.map((color, index) => (
                <div 
                  key={index}
                  className={`color-option ${selectedColor === color ? 'selected' : ''}`}
                  style={{ backgroundColor: color }}
                  onClick={() => setSelectedColor(color)}
                ></div>
              ))}
            </div>
          </div>
          
          {/* Quantity Selector */}
          <div className="quantity-selector">
            <h3>Quantity:</h3>
            <div className="quantity-control">
              <button onClick={decrementQuantity}>-</button>
              <span>{quantity}</span>
              <button onClick={incrementQuantity}>+</button>
            </div>
          </div>
          
          {/* Add to Cart Button */}
          <button 
            className="add-to-cart-btn"
            onClick={handleAddToCart}
          >
            ADD TO CART - ${(currentPrice * quantity).toFixed(2)}
          </button>
          
          {/* Product Meta */}
          <div className="product-meta">
            <div className="meta-item">
              <span>Category:</span>
              <span>{product.category || "N/A"}</span>
            </div>
            <div className="meta-item">
              <span>SKU:</span>
              <span>PRD-{product.id.toString().padStart(3, '0')}</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Singleproduct;
