import React from "react";
import { useParams } from "react-router-dom";
import Sdata from "./Sdata"; // ✅ Make sure this contains product data

const Single = () => {
  const { id } = useParams(); // ✅ Get product ID from URL
  const product = Sdata.shopItems.find((item) => item.id === parseInt(id)); // ✅ Find product

  if (!product) {
    return <h2>Product not found</h2>; // Handle case where product doesn't exist
  }

  return (
    <div>
      <h1>{product.name}</h1> {/* ✅ Show product title */}
    </div>
  );
};

export default SingleProduct;
