import React, { useState, useEffect } from 'react';
import { updateProduct } from '../../services/productService';

const EditProductForm = ({ product, onClose, onProductUpdated }) => {
  const [editedProduct, setEditedProduct] = useState({
    name: product.name || '',
    price: product.price || 0,
    description: product.description || '',
    discount: product.discount || 0,
    gender: product.gender || '',
    image_url: product.image_url || './images/shops/shop-1.png',
    size: product.size || 'M',
    stock: product.stock || 0,
    categoryId: product.categoryId || 2
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const getCategory = (gender) => {
    if (gender === 'men') return 'men';
    if (gender === 'women') return 'women';
    if (gender === 'kids') return 'kids';
    return '';
  };

  useEffect(() => {
    setEditedProduct({
      name: product.name || '',
      price: product.price || 0,
      description: product.description || '',
      discount: product.discount || 0,
      gender: product.gender || '',
      image_url: product.image_url || './images/shops/shop-1.png',
      size: product.size || 'M',
      stock: product.stock || 0,
      categoryId: product.categoryId || 2
    });
  }, [product]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;

    if (name === 'category') {
      let genderValue = '';
      if (value === 'men') genderValue = 'men';
      if (value === 'women') genderValue = 'women';
      if (value === 'kids') genderValue = 'kids';

      setEditedProduct({
        ...editedProduct,
        gender: genderValue
      });
    } else {
      setEditedProduct({
        ...editedProduct,
        [name]: name === 'price' || name === 'stock' || name === 'discount' ? Number(value) : value
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    const productDTO = {
      id: product.id,
      name: editedProduct.name,
      price: Number(editedProduct.price),
      description: editedProduct.description,
      discount: Number(editedProduct.discount),
      gender: editedProduct.gender,
      image_url: editedProduct.image_url,
      size: editedProduct.size,
      stock: Number(editedProduct.stock),
      categoryId: editedProduct.categoryId
    };

    try {
      const updatedProduct = await updateProduct(product.id, productDTO);
      console.log('Updated product:', updatedProduct);
      onProductUpdated(updatedProduct);
      onClose();
    } catch (err) {
      setError(`Failed to update product: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="popup-overlay">
      <div className="popup-content">
        <h3>Edit Product</h3>
        {error && <div className="error-message">{error}</div>}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="edit-name">Name</label>
            <input
              type="text"
              name="name"
              id="edit-name"
              value={editedProduct.name}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="edit-price">Price</label>
            <input
              type="number"
              name="price"
              id="edit-price"
              value={editedProduct.price}
              onChange={handleInputChange}
              required
              min="0"
            />
          </div>
          <div className="form-group">
            <label htmlFor="edit-category">Category</label>
            <select
              id="edit-category"
              name="category"
              value={getCategory(editedProduct.gender)}
              onChange={handleInputChange}
            >
              <option value="">Select category</option>
              <option value="men">Men</option>
              <option value="women">Women</option>
              <option value="kids">Kids</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="edit-description">Description</label>
            <textarea
              id="edit-description"
              name="description"
              rows="4"
              value={editedProduct.description}
              onChange={handleInputChange}
              required
            ></textarea>
          </div>
          <div className="form-group">
            <label htmlFor="edit-discount">Discount</label>
            <select
              id="edit-discount"
              name="discount"
              value={editedProduct.discount}
              onChange={handleInputChange}
            >
              <option value="0">No</option>
              <option value="5">5%</option>
              <option value="10">10%</option>
              <option value="20">20%</option>
              <option value="30">30%</option>
              <option value="40">40%</option>
              <option value="50">50%</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="edit-stock">Stock</label>
            <input
              type="number"
              name="stock"
              id="edit-stock"
              value={editedProduct.stock}
              onChange={handleInputChange}
              required
              min="0"
            />
          </div>
          <div className="form-buttons">
            <button
              type="submit"
              className="btn primary"
              disabled={loading}
            >
              {loading ? 'Updating...' : 'Update product'}
            </button>
            <button
              type="button"
              className="btn cancel"
              onClick={onClose}
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default EditProductForm;
